import React, { Fragment, useEffect, useState } from "react";
import firebase from "firebase";
import { auth } from "./firebase";
import "./Home.css";
import Content from "./Content";
import EditorApp from "./Editor";
import List from "./List";

const Home = (props: any) => {
	const [currentUser, setCurrentUser] = useState<null | object>(null);
	const [new_page, setNew_page] = useState(false);

	const [notes, setNotes] = useState<firebase.firestore.DocumentData[]>([]);
	const [selectedNoteIndex, setSelectedNoteIndex] = useState(null);
	const [selectedNote, setSelectedNote] = useState(null);
	const HandleOnclick = () => {
		setNew_page(true);
		console.log(new_page);
	};
	useEffect(() => {
		// if not logged in, redirect to login page
		auth.onAuthStateChanged((user) => {
			user ? setCurrentUser(user) : props.history.push("/login");

			firebase
				.firestore()
				.collection("notes")
				.onSnapshot((serverUpdate) => {
					const notes = serverUpdate.docs.map((doc) => {
						const data = doc.data();

						data["id"] = doc.id;

						return data;
					});

					setNotes(notes);
					console.log(notes);
				});
		});
	}, []);

	selectNote = (note, index) => {
		setSelectedNote(note);
		setSelectedNoteIndex(index);
	};

	const newNote = async (title: string) => {
		const note = {
			title,
			body: "",
		};
		const newFromDB = await firebase.firestore().collection("notes").add({
			title: note.title,
			body: note.body,
			timestamp: firebase.firestore.FieldValue.serverTimestamp(),
		});

		const newID = newFromDB.id;

		await setNotes(note);
		const newNoteIndex = this.state.notes.indexOf(
			this.state.notes.filter((_note) => _note.id === newID)[0]
		);

		this.setState({
			selectedNote: this.state.notes[newNoteIndex],
			selectedNoteIndex: newNoteIndex,
		});
	};

	return (
		<Fragment>
			<div className="Home">
				<header className="Home_header">notion clone</header>
				<button
					className="Heme_logout"
					onClick={async (event) => {
						try {
							await auth.signOut();
							props.history.push("/login");
						} catch (error) {
							alert(error);
						}
					}}
					style={{ marginTop: "0.5em", marginBottom: "0.5em" }}
				>
					Logout
				</button>
				<div className="Home_main">
					<div className="list">
						list
						{<List newNote={newNote} />}
					</div>
					<div className="contents">
						{new_page && <Content />}
						{new_page && <EditorApp />}
					</div>
				</div>
				<button className="Home_newpage" onClick={HandleOnclick}>
					+ New Page
				</button>
			</div>
		</Fragment>
	);
};

export default Home;
