import React, { Fragment, useEffect, useState } from "react";

function List(props: any) {
	const { classes, notes, selectedNoteIndex } = props;
	const [addingNote, setAddingNote] = useState(false);
	const [title, setTitle] = useState<HTMLInputElement>(null);

	const newNoteBtnClick = () => {
		setAddingNote(!addingNote);
		console.log(addingNote);
	};

	const updateTitle = (txt: React.KeyboardEventHandler<HTMLInputElement>) => {
		setTitle(txt);
	};

	return (
		<div>
			{addingNote ? (
				<div>
					<input
						type="text"
						//   className={newNoteInput}
						placeholder="ノートのタイトル"
						onKeyUp={updateTitle}
					/>
					<button
					// className={classes.newNoteSubmitBtn}
					// onClick={this.newNote}
					>
						ノートを作成する
					</button>
				</div>
			) : null}
			<button onClick={newNoteBtnClick}>
				{addingNote ? "キャンセル" : "新規ノート"}
			</button>
		</div>
	);
}

export default List;
