import React, { useState } from "react";
import ReactQuill from "react-quill";
import _ from "lodash";
import {
	Editor,
	EditorState,
	RichUtils,
	convertToRaw,
	ContentState,
} from "draft-js";
import "draft-js/dist/Draft.css";
import { stateToHTML } from "draft-js-export-html";
import { render } from "@testing-library/react";

function EditorApp(props: any) {
	const [text, setText] = useState("");
	// const [title, setTitle] = useState("");
	// const [id, setId] = useState("");
	const note = props.note;
	const noteUpdate = props.noteUpdate;

	// const updateBody = async (val: string) => {
	// 	await setText(val);
	// 	console.log(text);
	// };
	const update = _.debounce(() => {
		const { noteUpdate } = this.props;
		const { id, title, text } = this.state;

		noteUpdate(id, {
			title,
			body: text,
		});
	}, 1500);

	return (
		<div>
			<input
				// className={classes.titleInput}
				placeholder="Untitled"
				value={note.title ? note.title : ""}
				// onChange={(e) => this.updateTitle(e.target.value)}
			/>
			<ReactQuill
				value={text}
				onChange={props.noteUpdate(note.id, note)}
			></ReactQuill>
			<input
				// className={classes.titleInput}
				placeholder="content"
				value={note.body ? note.body : ""}
				// onChange={(e) => this.updateTitle(e.target.value)}
			/>
		</div>
	);
}

export default EditorApp;
